mapper.select:
SELECT * FROM tw_operation_log
WHERE team_id = #{teamId}
ORDER BY created_at DESC
LIMIT #{limit}

SELECT * FROM tw_operation_log
WHERE team_id = #{teamId}

    AND event_type = #{eventTypeEnum}


    AND user_id = #{userId}


    AND created_at >= #{startTime}


    AND created_at <= #{endTime}

ORDER BY created_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM tw_operation_log
WHERE team_id = #{teamId}

    AND event_type = #{eventTypeEnum}


    AND user_id = #{userId}


    AND created_at >= #{startTime}


    AND created_at <= #{endTime}
